mapper.select:
SELECT review_id, user_id, store_id, image_address, score, date, num_comments, liked
FROM review
WHERE review_id = #{review_id}

mapper.update:
UPDATE review
SET user_id = #{user_id}, store_id = #{store_id}, image_address = #{image_address}, score = #{score}, date = #{date}, num_comments = #{num_comments}, liked = #{liked}
WHERE review_id = #{review_id}
